mapper.update:
update follow_project
set del_flag='1'
where user_id=#{userId,jdbcType=INTEGER}
and
project_id=#{projectId,jdbcType=INTEGER}

mapper.select:
	select id, user_id,project_id,create_time,del_flag
	from follow_project
  where user_id=#{userId,jdbcType=INTEGER}
  and
  project_id=#{projectId,jdbcType=INTEGER}

mapper.update:
update follow_project
set del_flag='0'
where user_id=#{userId,jdbcType=INTEGER}
and
project_id=#{projectId,jdbcType=INTEGER}
